import React from 'react';
import styled from 'styled-components';
import { EACCES } from 'constants';

const DeFiCard = styled.div`
                        width: 300px;
                        display: flex;
                        flex-direction: column;
                        font-size: var(--text-prettysmall);
                        padding: 1% 2%;
                        margin: 0 0.5%;
                        border-radius: 10px;
                        background-color: var(--white);
                        flex: 0 0 auto;
  }
`;

const Row = styled.span`
                        display: flex;
                        flex-grow: 1;
                        width: 100%;
                        padding: 4% 0;
  }
`;

const LogoRow = styled.span`
                        display: flex;
                        flex-grow: 1;
                        width: 100%;
                        padding: 4% 0;
                        margin-bottom: 10px;
  }
`;

const Left = styled.span`
                      display: flex;
                      flex-grow: 1;
                      text-align: left;
                      font-weight: 700;
                      align-items: center;
  }
`;

const Right = styled.span`
                      display: flex;
                      text-align: right;
                      align-items: center;
  }
`;


const Card = (props) => {
    return (
<DeFiCard>
<LogoRow>
<Left>
<img src={ props.url } alt="Protocol Logo" border="0" height="26px"></img>
</Left>
<Right><a href={ props.link }>show more</a></Right>
</LogoRow>
    <Row>
    <Left>{ props.balanceLabel }</Left>
    <Right>{ props.balanceAmount }</Right>
    </Row>
    <Row>
    <Left>{ props.aprLabel }</Left>
    <Right>{ props.aprAmount }</Right>
    </Row>
</DeFiCard>  
        );
  }

  export default Card;
