import React from 'react';
import styled from 'styled-components';

const Network = styled.div`
                        width: 84%;
                        display: flex;
                        flex-direction: column;
                        padding: 8%;
                        margin: 0 0.5%;
                        border-radius: 10px;
                        flex: 0 0 auto;
                        height: auto;
                        color: #fff;
                        flex-wrap: wrap;
                        background-repeat: no-repeat, repeat;
                        background-position: center; 
                        background-size: cover; 
                        font-family: var(--secondary-font);



  }
`;

const Supporter = styled.span`
                      padding: 2px 0 2px 7px;
                      text-align: left;
                      font-weight: 500;
                      font-size: 0.6rem;
                      align-items: center;
                      background-color: CC1B28;
                      color: #000;
                      border-radius:10px;
                      width:6rem;


  }
`;

const LogoRow = styled.span`
                        display: flex;
                        width: 100%;  }
`;

const NetworkName = styled.span`
                      display: flex;
                      flex-grow: 1;
                      text-align: left;
                      font-size: 2.6rem;
                      font-weight: 500;
                      align-items: center;
  }
`;

const Balance = styled.span`
                      display: flex;
                      text-align: left;
                      font-weight: 300;
                      font-size: 1.3rem;
                      align-items: center;
  }
`;

const Address = styled.span`
                      display: flex;
                      text-align: left;
                      font-weight: 300;
                      font-size: 0.8rem;
                      align-items: center;
                      margin-top: 2.6rem;
  }
`;

const Logo = styled.span`
                      display: flex;
                      text-align: right;
                      align-items: center;
  }
`;


const WalletCard = (props) => {
    return (
<Network style={{ backgroundImage: `url(${props.backgroundImage})` }}>
<Supporter>{ props.supporter }</Supporter>
<LogoRow>
<NetworkName>{ props.networkName }</NetworkName>
<Logo><img src={ props.url } alt="Protocol Logo" border="0" height="70px"></img></Logo>
</LogoRow>
    <Balance>{ props.balance }</Balance>
    <Address>{ props.address }</Address>
</Network>  
        );
  }

  export default WalletCard;
