import React from 'react';
import styled from 'styled-components';

const TokenCard = styled.span`
                          width: 100%;
                          padding: 2% 0;
                          margin: 1% 0 0.5% 0;
                          display: flex;
                          flex-direction: row;
                          font-size: var(--text-prettysmall);
                          border-radius: 10px;
                          background-color: var(--white);
  }
`;


const AssetLogo = styled.span`
  width: 5%;
  text-align: center;
  align-items: center;
  }
`;


const AssetName = styled.span`
  width: 20%;
  display: flex;
  align-items: center;
  }
`;

const AssetAmount = styled.span`
  width: 24%;
  display: flex;
  align-items: center;
  }
`;


const AssetPrice = styled.span`
  width: 16%;
  display: flex;
  align-items: center;
  }
`;

const AssetPriceVariation = styled.span`
  width: 10%;
  display: flex;
  align-items: center;
  justify-content: center;
  }
`;


const AssetValue = styled.span`
  width: 23%;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  font-weight: 700;
`;

const Token = (props) => {
    return (
<TokenCard>
      <AssetLogo><img src={ props.url } alt="Token Logo" border="0" height="26px"></img></AssetLogo>
      <AssetName>{ props.assetName }</AssetName>
      <AssetAmount>{ props.assetAmount }</AssetAmount>
      <AssetPrice>{ props.assetPrice }</AssetPrice>
      <AssetPriceVariation>{ props.assetPriceVariation }</AssetPriceVariation>
      <AssetValue>{ props.assetValue }</AssetValue>
</TokenCard>
        );
  }

  export default Token;
