import React from 'react';
import styled from 'styled-components';

const Network = styled.div`
                        width: 90%;
                        display: flex;
                        flex-direction: column;
                        font-size: var(--text-prettysmall);
                        padding: 2% 5%;
                        margin: 0 0.5%;
                        border-radius: 10px;
                        background-color: var(--white);
                        flex: 0 0 auto;
                        height: auto;
                        min-height: 10rem;
                        background-color: ${props.bg};

  }
`;

const Row = styled.span`
                        display: flex;
                        flex-grow: 1;
                        width: 100%;
                        padding: 4% 0;
  }
`;

const LogoRow = styled.span`
                        display: flex;
                        flex-grow: 1;
                        width: 100%;
                        padding: 4% 0;
                        margin-bottom: 10px;
  }
`;

const NetworkName = styled.span`
                      display: flex;
                      flex-grow: 1;
                      text-align: left;
                      font-weight: 700;
                      align-items: center;
  }
`;

const Left = styled.span`
                      display: flex;
                      flex-grow: 1;
                      text-align: left;
                      font-weight: 700;
                      align-items: center;
  }
`;

const Right = styled.span`
                      display: flex;
                      text-align: right;
                      align-items: center;
  }
`;


const WalletCard = (props) => {
    return (
<Network>
<LogoRow>
<NetworkName>{ props.networkName }</NetworkName>
<Right><img src={ props.url } alt="Protocol Logo" border="0" height="26px"></img></Right>
</LogoRow>
    <Row>
    <Left>{ props.balance }</Left>
    </Row>
    <Row>
    <Left>{ props.address }</Left>
    </Row>
</Network>  
        );
  }

  export default WalletCard;
