import React from 'react';
import styled from 'styled-components';

const DeFiCard = styled.div`
                        width: 300px;
                        display: flex;
                        flex-direction: column;
                        font-size: var(--text-prettysmall);
                        padding: 1% 2%;
                        margin: 0 0.5%;
                        border-radius: 10px;
                        background-color: var(--white);
                        flex: 0 0 auto;
  }
`;

const Row = styled.span`
                        display: flex;
                        flex-grow: 1;
                        width: 100%;
                        padding: 4% 0;
  }
`;

const Left = styled.span`
                      display: flex;
                      flex-grow: 1;
                      text-align: left;
                      font-weight: 700;
                      align-items: center;
  }
`;

const Right = styled.span`
                      display: flex;
                      text-align: right;
                      align-items: center;
  }
`;


const Card = (props) => {
    return (
<DeFiCard>
    <Row>
    <Left></Left>
    <Right></Right>

    </Row>
</DeFiCard>  
        );
  }

  export default Card;
