import React from 'react';
import styled from 'styled-components';


const RowContainer = styled.span`
                display: flex;
                  justify-content: space-between;
                  width: 100%;
                  padding: 2% 0;                
`;
const Left = styled.span`
               display: flex;
                  flex-grow: 1;
                  text-align: left;
                  font-weight: 700;
                  align-items: center;               
`;

const Right = styled.span`
               display: flex;
                  flex-grow: 3;
                  flex-direction: row-reverse;
                  padding: 1.5% 0;
                  align-items: center;              
`;


const Form = styled.form`
               display: flex;
                            justify-content: space-between;
                            align-items: center;
                            border:1px solid #E0E0E0;
                            border-radius: 5px;
                            background-color: #000;
                            text-align: right;  

                            @media (max-width: 800px) {
                    width: 80%;
                    display: flex;
                    flex-grow: 2;
                    justify-content: space-between;
                    align-items: center;
                    border:1px solid #E0E0E0;
                    border-radius: 5px;
                    background-color: #F2F2F2;
                    text-align: right;
  }
`;

const AmountInput = styled.input`
                              display: flex;
                              width: 74%;
                              background-color: #F2F2F2;
                              padding: 20px 15px 25px;

                              ::placeholder,
  ::-webkit-input-placeholder {
                              font-family: var(--main-font);
                              font-weight: 300;
                              color: var(--light-black);
  }

  :focus {
    outline: none;
  }

  :focus::placeholder {
                            color:transparent;
                       }


`;

const ButtonMax = styled.button`
                display: flex;
                            height: 30px;
                            background: #4A4A4A;
                            color: white;
                            font-size: var(--text-small);
                            text-align: center;
                            width: 17%;
                            padding: 2% 3% 2% 4%;
                            margin: 0 3% 0 1%;
                            font-weight: 300;
                            border-radius: 2px;


`;




const CardAmount = (props) => {
    return (  
    <RowContainer>
    <Left>Amount</Left>
    <Right>
    <Form>
                                                <AmountInput placeholder="i.e 234,98.00 DAI"/>
                                                <ButtonMax>MAX</ButtonMax>
                                              </Form>
    </Right>
    </RowContainer>
        );
  }

  export default CardAmount;
